quipmentSlot, itemID, isBuffed)\nend\n\nfunction ItemWidget.ShowItemCooldown(allstates, equipmentSlot, itemID, isBuffed)\n    ItemWidget.db.item[itemID].onProc = isBuffed\n    local type = ItemWidget.db.item[itemID].type\n    if type == 0 then\n        ItemWidget.StartItemCooldown(allstates, equipmentSlot, itemID)\n    elseif type == 1 then\n        \n        ItemWidget.OnUse(allstates, equipmentSlot, itemID)\n        ItemWidget.StartItemCooldown(allstates, equipmentSlot, itemID)\n    elseif type == 2 then\n        ItemWidget.ClearAllTimers(allstates, equipmentSlot, itemID)\n    end\nend\n\nfunction ItemWidget.OnExtraProc(allstates, equipmentSlot, itemID, procID)\n    local _,_,_, stacks = WA_GetUnitBuff(\"player\", procID)\n    local stateName = stformat(\"IW_Slot%d\", equipmentSlot)\n    if allstates[stateName] then\n        allstates[stateName].stacks = stacks\n        return true\n    end\nend\n\nfunction ItemWidget.OnProc(allstates, equipmentSlot, itemID, procID)\n    if GetInventoryItemID(\"player\", equipmentSlot) == itemID then\n        local _,_, icon, stacks ,_, buffDuration, buffExpirationTime = WA_GetUnitBuff(\"player\", procID)\n        if ItemWidget.db.item[itemID].type == 0 then\n            local cooldown = ItemWidget.db.item[itemID].cooldown\n            ItemWidget.db.item[itemID].duration = cooldown\n            ItemWidget.db.item[itemID].expirationTime = buffExpirationTime - buffDuration + cooldown\n        end\n        ItemWidget.db.item[itemID].onProc = true\n        if config.settings_standart.proc_show then\n            local stateName = stformat(\"IW_Slot%d\", equipmentSlot)\n            local state = allstates[stateName]\n            state.duration = buffDuration\n            state.expirationTime = buffExpirationTime\n            state.icon = icon\n            state.stacks = stacks\n            state.onProc = true\n            state.changed = true\n            allstates[stateName] = state\n        else\n            ItemWidget.ShowItemCooldown(allstates, equipmentSlot, itemID, true)\n        end\n    end\nend\n\nfunction ItemWidget.SaveLastSession()\n    WeakAurasSaved[\"displays\"][ItemWidget.id][ItemWidget.myName] = {}\n    local db = {}\n    for itemID, itemData in pairs(ItemWidget.db.item) do\n        db[itemID] = {}\n        for element, value in pairs(itemData) do\n            db[itemID][element] = value\n        end\n    end\n    WeakAurasSaved[\"displays\"][aura_env.id][aura_env.myName] = db\nend\n\nfunction ItemWidget.LoadLastSession(allstates, equipmentSlot, itemID)\n    local db = WeakAurasSaved[\"displays\"][ItemWidget.id][ItemWidget.myName]\n    if db and db[itemID] then\n        local type = ItemWidget.db.item[itemID].type\n        local cooldown = ItemWidget.db.item[itemID].cooldown\n        local remainingTime = db[itemID].expirationTime - GetTime()\n        if remainingTime > 0\n        and ( (type == 2 and remainingTime < 30) or (type == 0 and remainingTime < cooldown) ) then\n            ItemWidget.db.item[itemID].duration = db[itemID].duration\n            ItemWidget.db.item[itemID].expirationTime = db[itemID].expirationTime\n            ItemWidget.StartItemCooldown(allstates, equipmentSlot, itemID)\n            return true\n        end\n    end\nend\n\nfunction ItemWidget.IsLastSession(equipmentSlot, itemID)\n    local db = WeakAurasSaved[\"displays\"][ItemWidget.id][ItemWidget.myName]\n    if db and db[itemID] then\n        local cooldown = ItemWidget.db.item[itemID].cooldown\n        local remainingTime = db[itemID].expirationTime - GetTime()\n        if remainingTime < cooldown and remainingTime > 30 then\n            return true\n        end\n    end\nend\n\nfunction ItemWidget.GetSlotItemInfo(equipmentSlot)\n    local itemLink = GetInventoryItemLink(\"player\", equipmentSlot)\n    if itemLink then\n        local itemString = select(3, strfind(itemLink, \"|H(.+)|h\"))\n        local _, itemID, enchantID = strsplit(\":\", itemString)\n        return tonumber(itemID), tonumber(enchantID)\n    end\nend\n\nfunction ItemWidget.AddExtraProcDB(itemID)\n    local extraProcID = ItemWidget.db.item[itemID].extraProc\n    if extraProcID then\n        ItemWidget.db.extraProc[extraProcID] = itemID\n    end\nend\n\nfunction ItemWidget.AddProcDB(itemID, EnchantProcID)\n    local function SetItemProc(itemID, procID)\n        ItemWidget.db.proc[procID] = itemID\n        table.insert(ItemWidget.db.item[itemID].procs, procID)\n        if not ItemWidget.db.item[itemID].duration and ItemWidget.db.item[itemID].type == 0 then\n            local cooldown = ItemWidget.GetCooldownTime(procID)\n            ItemWidget.db.item[itemID].cooldown = cooldown\n            if cooldown == 0 then\n                ItemWidget.db.item[itemID].type = 2\n            end\n        end\n    end\n    local procID = EnchantProcID or ItemWidget.ItemData[itemID]\n    local t = type(procID)\n    if t == \"number\" then\n        SetItemProc(itemID, procID)\n    elseif t == \"table\" then\n        for _, procID in ipairs(procID) do\n            SetItemProc(itemID, procID)\n        end\n    end\nend\n\nfunction ItemWidget.AddItemDB(equipmentSlot, itemID, enchantID)\n    ItemWidget.db.item[itemID] = {\n        procs = {},\n        extraProc = ItemWidget.ItemExtraProcs[itemID],\n        type = select(3, GetInventoryItemCooldown(\"player\", equipmentSlot)),\n        expirationTime = GetTime(),\n        icon = GetItemIcon(itemID),          \n    }\nend\n\nfunction ItemWidget.InitItemID(equipmentSlot, itemID, enchantID)\n    if not ItemWidget.db.item[itemID] then\n        local enchantID = ItemWidget.EnchantData[enchantID]\n        ItemWidget.AddItemDB(equipmentSlot, itemID, enchantID)\n        ItemWidget.AddProcDB(itemID, enchantID)\n        ItemWidget.AddExtraProcDB(itemID)\n    end\n    ItemWidget.db.item[itemID].equipmentSlot = equipmentSlot\nend\n\nfunction ItemWidget.InitEquipmentSlot(allstates, equipmentSlot)\n    local itemID, enchantID = ItemWidget.GetSlotItemInfo(equipmentSlot)\n    if itemID ~= nil then\n        if enchantID ~= 0 and ItemWidget.EnchantData[enchantID] or ItemWidget.ItemData[itemID] then\n            ItemWidget.db.equipmentSlot[equipmentSlot] = itemID\n            ItemWidget.InitItemID(equipmentSlot, itemID, enchantID)\n            ItemWidget.InitializeFrame(allstates, equipmentSlot, itemID)\n            return true\n        end\n    end\nend\n\nfunction ItemWidget.OnInit(allstates)\n    local updated = false\n    for equipmentSlot, data in pairs(ItemWidget.settings.equipmentSlot) do\n        if data.enable then\n            if ItemWidget.InitEquipmentSlot(allstates, equipmentSlot) then\n                updated = true\n            end\n        end\n    end\n    if not updated then return end\n    for equipmentSlot, itemID in pairs(ItemWidget.db.equipmentSlot) do\n        local isBuffed = false\n        for _, procID in ipairs(ItemWidget.db.item[itemID].procs) do\n            if not isBuffed then\n                local isProcID = WA_GetUnitBuff(\"player\", procID)\n                if isProcID then\n                    isBuffed = true\n                    ItemWidget.OnProc(allstates, equipmentSlot, itemID, procID)\n                end\n            end\n        end\n        if not isBuffed then\n            ItemWidget.db.item[itemID].onProc = false\n            local type = ItemWidget.db.item[itemID].type\n            if type == 0 or type == 2 then\n                ItemWidget.LoadLastSession(allstates, equipmentSlot, itemID)\n            else\n                ItemWidget.OnUse(allstates, equipmentSlot, itemID)\n                ItemWidget.StartItemCooldown(allstates, equipmentSlot, itemID)\n            end\n        else\n            local extraProcID = ItemWidget.db.item[itemID].extraProc\n            if extraProcID then\n                ItemWidget.OnExtraProc(allstates, equipmentSlot, itemID, extraProcID)\n            end\n        end\n    end\n    return updated\nend\n\nfunction ItemWidget.BagUpdateCooldown(allstates)\n    local update = false\n    for equipmentSlot in pairs(ItemWidget.settings.equipmentSlot) do\n        local itemID = GetInventoryItemID(\"player\", equipmentSlot)\n        if itemID and itemID == ItemWidget.db.equipmentSlot[equipmentSlot] then\n            \n            if ItemWidget.db.item[itemID].type == 1 and not ItemWidget.db.item[itemID].onProc then\n                if ItemWidget.OnUse(allstates, equipmentSlot, itemID) then\n                    ItemWidget.StartItemCooldown(allstates, equipmentSlot, itemID)\n                    update = true\n                end\n            end\n        end\n    end\n    return update\nend\n\nfunction ItemWidget.EquipmentSlotChanged(allstates, equipmentSlot, hasCurrent)\n    local timeStamp = GetTime()\n    if hasCurrent and ItemWidget.InitEquipmentSlot(allstates, equipmentSlot) then\n        local itemID = ItemWidget.db.equipmentSlot[equipmentSlot]\n        ItemWidget.db.item[itemID].onProc = false\n        local type = ItemWidget.db.item[itemID].type\n        if type ~= 1 then\n            if type == 0 and ItemWidget.IsLastSession(equipmentSlot, itemID) then\n                ItemWidget.LoadLastSession(allstates, equipmentSlot, itemID)\n            else\n                local remainingTime = ItemWidget.db.item[itemID].expirationTime - GetTime()\n                if remainingTime < 30 then\n                    ItemWidget.db.item[itemID].duration = 30\n                    ItemWidget.db.item[itemID].expirationTime = timeStamp + 30\n                end\n                ItemWidget.StartItemCooldown(allstates, equipmentSlot, itemID)\n            end\n        else\n            ItemWidget.OnUse(allstates, equipmentSlot, itemID)\n            ItemWidget.StartItemCooldown(allstates, equipmentSlot, itemID)\n        end\n        \n    elseif ItemWidget.db.equipmentSlot[equipmentSlot] then\n        ItemWidget.ClearFrame(allstates, equipmentSlot)\n    end\nend",
