   = true,\n}\n\nItemWidget.GetCooldownTime = function(spellID)\n    local t = ItemWidget.cooldowns[spellID]\n    return t or 45\nend\n\nlocal config = ItemWidget.config\nlocal pairs, ipairs, stformat = pairs, ipairs, string.format\nlocal GetInventoryItemID, GetInventoryItemCooldown, GetInventoryItemLink = GetInventoryItemID, GetInventoryItemCooldown, GetInventoryItemLink\nlocal GetTime = GetTime\n\nItemWidget.tinsert = table.insert\nItemWidget.substr = string.sub\nItemWidget.myGUID = UnitGUID(\"player\")\nItemWidget.myName = UnitName(\"player\")\n\nlocal function InitializeConfig()\n    local validEquiptmentSlots = {\n        [1] = { 13, 14 },   -- Trinkets\n        [2] = { 11, 12 },   -- Ashen Ring\n        [3] = { 16 },       -- Main Weapon\n        [4] = { 10 },       -- Gloves\n        [5] = { 8 },        -- Boots (Nitro Boots)\n        [6] = { 15 },       -- Back\n        [7] = { 18 },       -- Relic\n    }\n    ItemWidget.settings = { equipmentSlot = {} }\n    for i, value in ipairs(config.settings_standart.validEquiptmentSlots) do\n        if value then\n            for _, equipmentSlot in ipairs(validEquiptmentSlots[i]) do\n                aura_env.settings.equipmentSlot[equipmentSlot] = {\n                    enable = true\n                }\n            end\n        end\n    end\n    if config.settings_advanced.enable then\n        local validEnchants = {\n            [1] = 3859,   -- Springy Arachnoweave (Parachute)\n            [2] = 3722,   -- Lightweave Embroidery\n            [3] = 3728,   -- Darkglow Embroidery\n            [4] = 3730,   -- Swordguard Embroidery\n            [5] = 3790,   -- Black Magic\n            [6] = 3603,   -- Hand-Mounted Pyro Rocket\n            [7] = 3604,   -- Hyperspeed Accelerators\n            [8] = 3606,   -- Nitro Boots (merfin)\n            [9] = 3789,    -- Berserking (belden)\n            [10] = 2673,    -- Mongoose\n            [11] = 3869,    -- Blade Ward\n            [12] = 3870,    -- Blood Draining\n         