						["custom_type"] = "stateupdate",
						["custom_hide"] = "timed",
						["customVariables"] = "{\n    expirationTime = true,\n    \n    onProc = {\n        display = \"On Proc Buff\",\n        type = \"bool\",\n    },\n    \n    itemType = {\n        display = \"Item Type\",\n        type = \"select\",\n        values = {\n            [0] = \"Item with ICD mechanic\",\n            [1] = \"Item with On Use mechanic\",\n            [2] = \"Item without CD\",\n        }\n    },\n    \n    showProc = {\n        display = \"Show Proc Option\",\n        type = \"select\",\n        values = {\n            [0] = \"Disabled\",\n            [1] = \"Enabled\"\n        }\n    }\n    \n}",
						["event"] = "Health",
						["subeventPrefix"] = "SPELL",
						["unit"] = "player",
						["custom"] = "function(allstates, event, ...)\n    if event == \"OPTIONS\" then\n        if WeakAuras.IsOptionsOpen() and aura_env.initialized then\n            aura_env.initialized = false\n            aura_env.SaveLastSession()\n        end\n        aura_env.OnInit(allstates)\n        \n    elseif event == \"WA_ITEM_WIDGET_FLAG\" then\n        aura_env.initialized = true\n        WeakAuras.ScanEvents(\"MERFIN_ITEM_WIDGET\", \"UPDATE\")\n        \n    elseif aura_env.initialized then\n        \n        if event == \"MERFIN_ITEM_WIDGET\" then\n            local subEvent = ...\n            if subEvent == \"UPDATE\" then\n                return aura_env.OnInit(allstates)    \n            end\n        elseif event == \"PLAYER_ALIVE\" then\n            return aura_env.OnInit(allstates)\n            \n        elseif event == \"PLAYER_EQUIPMENT_CHANGED\" then\n            local equipmentSlot, hasCurrent = ...\n            if aura_env.settings.equipmentSlot[equipmentSlot] then\n                aura_env.EquipmentSlotChanged(allstates, equipmentSlot, hasCurrent)\n                return true\n            end\n            \n        elseif event == \"BAG_UPDATE_COOLDOWN\" then\n            return aura_env.BagUpdateCooldown(allstates)\n            \n        elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n            local _, subEvent, srcGUID, _,_, destGUID, _,_, procID = ...\n            if ((destGUID == aura_env.myGUID and (srcGUID == nil or srcGUID == destGUID)) or srcGUID == aura_env.myGUID) \n            and not aura_env.INVALID_EVENTS[subEvent] \n            and (aura_env.db.proc[procID] or aura_env.db.extraProc[procID])\n            and aura_env.substr(subEvent, 0, 6) == \"SPELL_\" then\n                if aura_env.db.proc[procID] then\n                    -- Main Proc Trigger\n                    local itemID = aura_env.db.proc[procID]\n                    local equipmentSlot = aura_env.db.item[itemID].equipmentSlot\n                    local type = aura_env.db.item[itemID].type\n                    \n                    if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_REFRESH\" or subEvent == \"SPELL_AURA_APPLIED_DOSE\" then\n                        aura_env.OnProc(allstates, equipmentSlot, itemID, procID)\n                    elseif not aura_env.db.extraProc[procID] then\n                        if subEvent == \"SPELL_AURA_REMOVED\" then\n                            aura_env.ShowItemCooldown(allstates, equipmentSlot, itemID, false)\n                        elseif type == 0 then\n                            aura_env.StartInternalTimer(allstates, equipmentSlot, itemID)\n                        end\n                    end\n                    \n                    return true\n                else \n                    local itemID = aura_env.db.extraProc[procID]\n                    local equipmentSlot = aura_env.db.item[itemID].equipmentSlot\n                    \n                    -- Extra Proc Trigger\n                    if subEvent == \"SPELL_AURA_APPLIED\" or subEvent == \"SPELL_AURA_REFRESH\" \n                    or subEvent == \"SPELL_AURA_APPLIED_DOSE\" or subEvent == \"SPELL_AURA_REMOVED\" then\n                        return aura_env.OnExtraProc(allstates, equipmentSlot, itemID, procID)\n                    end\n                end\n            end\n            \n        elseif event == \"PLAYER_LOGOUT\" then\n            aura_env.SaveLastSession()\n        end\n    end\nend",
