n        return aura_env.classIcons[class..aura_env.config.IconStyle]\n    end\nend\n\n\n\naura_env.InitLibrary()\n\nfunction aura_env.RecacheHealers()\n    aura_env.healers = {}\n    \n    for unit in WA_IterateGroupMembers() do\n        if aura_env.libGT:GetUnitRole(unit) == \"healer\" then\n            aura_env.healers[unit] = true\n        end\n    end  \nend\n\n\naura_env.drinktest = function(unit)\n    if UnitExists(unit) then\n        local _, _, icon = UnitAura(unit, \"Drink\")\n        if icon then\n            return true\n        end\n    end\n    return false\nend\n\naura_env.cacheEvents = {\n    [\"PARTY_MEMBERS_CHANGED\"] = true,\n    [\"RAID_ROSTER_UPDATE\"] = true,\n    [\"ACTIVE_TALENT_GROUP_CHANGED\"] = true,\n    [\"OPTIONS\"] = true,\n    [\"LibGroupTalents_RoleChange\"] = true,\n}",
